var usr = current.variables.requested_for;
var count = 0;
var app1sysid = "";
var application1 = new GlideRecord('x_14308_uam_user_a_user_to_application');
	application1.addQuery('user',usr);
		application1.addEncodedQuery('application=335705ebdb7213004b8de04a489619ac^active=true');
	application1.query();
		if(application1.next())
			{
				app1sysid = application1.sys_id;
				//gs.addInfoMessage(app1sysid);
			}
	var access = new GlideRecord('x_14308_uam_user_a_user_to_access');
	access.addQuery('user', usr);
	access.addQuery('application', app1sysid);
	access.addQuery('active',true);
	access.query();
	//gs.addInfoMessage(access.access);
	// Check if an Active access already exist for the user, do nothing
	while(access.next())
		{
			count = count +1;
	}
gs.addInfoMessage(count);

if(count <= 0)
	{
		gs.addInfoMessage('inside applciation if');
	var application = new GlideRecord('x_14308_uam_user_a_user_to_application');
	application.addQuery('user',usr);
		application.addEncodedQuery('application=335705ebdb7213004b8de04a489619ac^active=true');
	application.query();
		if(application.next())
			{
				application.active = false;
				application.removed_date = new GlideDateTime();
				application.removed_ritm = current.sys_id;
				application.update();
			}
	}

