var usr = current.variables.requested_for;
var flag = false;
var list = current.variables.select_access.toString();
flag = false;
var application = "";
var listArr = list.split(',');

for (var key in listArr) {
	
	// Get the list of Access from the catalog
	var access = new GlideRecord('x_14308_uam_user_a_user_to_access');
	access.addQuery('user', usr);
	//access.addQuery('application', access_parent);
	access.addQuery('access', listArr[key]);
	access.addQuery('active',true);
	access.query();
	//gs.addInfoMessage(access.access);
	// Check if an Active access already exist for the user, do nothing
	while(access.next())
		{
			application = access.application;
			access.active = false;
			access.removed_date = new GlideDateTime();
			access.removed_ritm = current.sys_id;
			access.update();
	}

}

