// Set values for the task in this script.  Use the variable 'task' when setting additional values.
// Note: This script is run after the task values are set using the Fields, Template or Values you have specified.
//
// For example:
//     task.short_description = current.short_description;

//var app  = workflow.scratchpad.application;
var appl  ='335705ebdb7213004b8de04a489619ac';
var usr = current.variables.requested_for;
//gs.addInfoMessage(usr);
var flag = false;
//gs.addInfoMessage(app);
var app = new GlideRecord('x_14308_uam_user_a_user_to_application');
app.addQuery('application', appl);
app.addQuery('user', usr);
app.addQuery('active',true);
app.query();

//Check if an Active application already exist in the table do nothing
if (app.next())
	{
		flag = true;
		//gs.addInfoMessage('Entry Present');
	}
else// If the application does not exist, create a new record
	{
		//gs.addInfoMessage('entry not present');
	var gr = new GlideRecord('x_14308_uam_user_a_user_to_application');
	gr.initialize();
	gr.application = appl;
	gr.user = current.variables.requested_for;
	gr.active = true;
	gr.granted_date = new GlideDateTime();
	gr.granted_ritm = current.sys_id;
	gr.insert();
		//gs.addInfoMessage('entry to application done');
}


//var app = workflow.scratchpad.application;
//var usr = current.variables.requested_for;
var list = current.variables.select_access.toString();
flag = false;
var access_parent = '';
var par = new GlideRecord('x_14308_uam_user_a_user_to_application');
	par.addQuery('active', 'true');
	par.addQuery('user', usr);
	par.addQuery('application', appl);
	par.query();
	
	if(par.next())
		{
		access_parent = par.sys_id;
	}
var listArr = list.split(',');
//gs.addInfoMessage(listArr);
for (var key in listArr) {
	
	// Get the list of Access from the catalog
	var access = new GlideRecord('x_14308_uam_user_a_user_to_access');
	access.addQuery('user', usr);
	access.addQuery('application', access_parent);
	access.addQuery('access', listArr[key]);
	access.addQuery('active',true);
	access.query();
	
	// Check if an Active access already exist for the user, do nothing
	if(access.next())
		{
			flag = true;		
	}
	
	else if(access_parent) // if the access does not exist or is inactive, create a new record
		{
		var gr2 = new GlideRecord('x_14308_uam_user_a_user_to_access');
		gr2.initialize();
		gr2.access = listArr[key];
		gr2.user = usr;
		gr2.granted_ritm = current.sys_id;
		gr2.active = true;
		gr2.granted_date = new GlideDateTime();
		gr2.application = access_parent;
		gr2.insert();
			//gs.addInfoMessage('entry to access done');
	}
}

